import React, {useRef} from "react";
import styles from './CalendarPresent.module.css';

function CalendarPresent({year, month, cellsArr}) {
    const monthObj = {
        0: ['Січень', 'isWinter'],
        1: 'Лютий',
        2: 'Березень',
        3: 'Квітень',
        4: 'Травень',
        5: 'Червень',
        6: 'Липень',
        7: 'Серпень',
        8: 'Вересень',
        9: 'Жовтень',
        10: ['Листопад', 'isAutumn'],
        11: 'Грудень',

    }

        const copy = [...cellsArr];
        const firstPart = useRef(copy.splice(0, 7));
        const secondPart = useRef(copy.splice(0, 7));
        const thirdPart = useRef(copy.splice(0, 7));
        const fourthPart = useRef(copy.splice(0, 7));
        const fifthPart = useRef(copy.splice(0, 7));
        const sixPart = useRef(copy.splice(0, 7));
        const cellsArrDivided = useRef([]);
        cellsArrDivided.current.push(firstPart.current, secondPart.current, thirdPart.current, fourthPart.current, fifthPart.current, sixPart.current);
        console.log(cellsArrDivided.current);


    function Rows() {
        const res = cellsArrDivided.current.map((elem, index) => {
           for(let i = 0; i < elem.length; i++) {
            //console.log(elem[i].value, index);
           return <tr key={index}> 
           <td>{elem[0].value}</td> <td>{elem[1].value}</td> <td>{elem[2].value}</td> <td>{elem[3].value}</td>
           <td>{elem[4].value}</td> <td class={styles.isWeekend}>{elem[5].value}</td> <td class={styles.isWeekend}>{elem[6].value}</td>
           </tr>
           }
        }) 
        return res;
    }


return <div>
    <h2>{monthObj[month][0]} {year}</h2>
    <table className={styles[monthObj[month][1]]}>
        <thead>
            <tr>
                <td>Пн</td> <td>Вт</td> <td>Ср</td> <td>Чт</td> <td>Пт</td> 
                <td class={styles.isWeekend}>Сб</td> <td class={styles.isWeekend}>Нд</td>
            </tr>
        </thead>
        <tbody>
           <Rows />
        </tbody>
    </table>
    <div className={styles.buttons}>
        <button>Назад</button>
        <button>Вперед</button>
    </div>
</div>
}

export default CalendarPresent;
