import React from "react";
import styles from './CalendarPresent.module.css';

function CalendarPresent({year, month, cellsArr}) {
    const monthObj = {
        0: 'Січень',
        1: 'Лютий',
        2: 'Березень',
        3: 'Квітень',
        4: 'Травень',
        5: 'Червень',
        6: 'Липень',
        7: 'Серпень',
        8: 'Вересень',
        9: 'Жовтень',
        10: 'Листопад',
        11: 'Грудень',

    }

        const copy = [...cellsArr];
        const firstPart = copy.splice(0, 7);
        const secondPart = copy.splice(0, 7);
        const thirdPart = copy.splice(0, 7);
        const fourthPart = copy.splice(0, 7);
        const fifthPart = copy.splice(0, 7);
        const sixPart = copy.splice(0, 7);
        const cellsArrDivided = [];
        cellsArrDivided.push(firstPart, secondPart, thirdPart, fourthPart, fifthPart, sixPart);
        console.log(cellsArrDivided);


    function Row1() {
        const res = cellsArrDivided.map((elem, index) => {
           for(let i = 0; i < elem.length; i++) {
            //console.log(elem[i].value, index);
           return <tr key={index}> 
           <td>{elem[0].value}</td> <td>{elem[1].value}</td> <td>{elem[2].value}</td> <td>{elem[3].value}</td>
           <td>{elem[4].value}</td> <td className='isWeekend'>{elem[5].value}</td> <td className='isWeekend'>{elem[6].value}</td>
           </tr>
           }
        }) 
        return res;
    }


return <div>
    <h2>{monthObj[month]} {year}</h2>
    <table>
        <thead>
            <tr>
                <td>Пн</td> <td>Вт</td> <td>Ср</td> <td>Чт</td> <td>Пт</td> <td>Сб</td> <td>Нд</td>
            </tr>
        </thead>
        <tbody>
           <Row1 />
        </tbody>
    </table>
    <div className={styles.buttons}>
        <button>Назад</button>
        <button>Вперед</button>
    </div>
</div>
}

export default CalendarPresent;
