import React, {useState, useRef} from "react";
import CalendarPresent from "./CalendarPresent";

function Calendar() {
    const now = useRef(new Date());
    const year = useRef(now.current.getFullYear());
    const month = useRef(now.current.getMonth());
    const [date, setDate] = useState(now.current.getDate());

    const lastDate = new Date(year.current, month.current + 1, 0);
    const lastDay = lastDate.getDate();
    const firstDate = new Date(year.current, month.current, 1);
    const firstDayOfWeek = firstDate.getDay();
    const refCellsAmount = useRef(firstDayOfWeek + lastDay);
    const refCounterCells = useRef(1);
    const refCounterDays = useRef(1);

    while (refCellsAmount.current % 7 !== 0) {
        refCellsAmount.current = refCellsAmount.current + 1;
    }
    
    const firstCellsArr = useRef([]);
    for(let i = refCellsAmount.current; i >= 1; i--) {
        firstCellsArr.current.push({
            id: year.current.toString() + (month.current + 1) + date + refCounterCells.current,
            value: refCounterCells.current >= firstDayOfWeek && refCounterCells.current < lastDay + firstDayOfWeek ? refCounterDays.current : null,
            hasTasks: false,
            allTasksDone: false,
            isActive: false,
            isToday: false,
        })
        if(refCounterCells.current >= firstDayOfWeek && refCounterCells.current < lastDay + firstDayOfWeek) {
            refCounterDays.current = refCounterDays.current + 1;
        }
        refCounterCells.current = refCounterCells.current + 1;

    }
    refCounterCells.current = 1;

    for(let elem of firstCellsArr.current) {
        elem.value === date ? elem.isToday = true : elem.isToday = false;
    } 

    const [cellsArr, setCellsArr] = useState(firstCellsArr.current);
    //console.log(cellsArr);

    function handleClickForward() {
        //console.log(new Date (year, month + 1, date));
        now.current = (new Date (year.current, month.current + 1, date));
        //console.log(year, month, date);
        year.current = now.current.getFullYear();
        month.current = now.current.getMonth();
        setDate(now.current.getDate());
        console.log(year.current, month.current, date);
        //setCellsArr(firstCellsArr.current);
    }

    return <div>
        <CalendarPresent year = {year.current} month = {month.current} cellsArr = {cellsArr} handleClickForward = {handleClickForward} />
    </div>

}

export default Calendar;
