import React, {useState, useRef} from "react";
import CalendarPresent from "./CalendarPresent";

function Calendar() {
    const now = new Date();
    const [year, setYear] = useState(now.getFullYear());
    const [month, setMonth] = useState(now.getMonth());
    const [date, setDate] = useState(now.getDate());
    const [day, setDay] = useState(now.getDay());
    const lastDate = new Date(year, month + 1, 0);
    const lastDay = lastDate.getDate();
    const refCellsAmount = useRef(day + lastDay);
    const refCounter = useRef(1);

    while (refCellsAmount.current % 7 !== 0) {
        refCellsAmount.current = refCellsAmount.current + 1;
    }
    
    const firstCellsArr = [];
    for(let i = refCellsAmount.current; i >= 1; i--) {
        firstCellsArr.push({
            id: year.toString() + (month + 1) + date + refCounter.current,
            value: '',
            hasTasks: false,
            allTasksDone: false,
            isActive: false,
        })
        refCounter.current = refCounter.current + 1;
    }
    refCounter.current = 1;
    

    const [cellsArr, setCellsArr] = useState(firstCellsArr);
    console.log(cellsArr);

    
    
    


    return <div>
        <CalendarPresent year = {year} month = {month} day = {day} cellsArr = {cellsArr} />
        
    </div>

}

export default Calendar;
