import React from "react";
import styles from './CalendarPresent.module.css';

function CalendarPresent({year, month, cellsArr, handleClickForward, handleClickBack}) {
    const monthObj = {
        0: ['Січень', 'isWinter'],
        1: ['Лютий', 'isWinter'],
        2: ['Березень', 'isSpring'],
        3: ['Квітень', 'isSpring'],
        4: ['Травень', 'isSpring'],
        5: ['Червень', 'isSummer'],
        6: ['Липень', 'isSummer'],
        7: ['Серпень', 'isSummer'],
        8: ['Вересень', 'isAutumn'],
        9: ['Жовтень', 'isAutumn'],
        10: ['Листопад', 'isAutumn'],
        11: ['Грудень', 'isWinter'],

    }


    function Rows() {

        const copy = [...cellsArr];
        const firstPart = copy.splice(0, 7);
        const secondPart = copy.splice(0, 7);
        const thirdPart = copy.splice(0, 7);
        const fourthPart = copy.splice(0, 7);
        const fifthPart = copy.splice(0, 7);
        const sixPart = copy.splice(0, 7);
        const cellsArrDivided = [];
        cellsArrDivided.push(firstPart, secondPart, thirdPart, fourthPart, fifthPart, sixPart);
        //console.log(cellsArrDivided);

        function styleForToday(data) {
            const style = styles.isToday;
            if(data.isToday) {
                return style;
            } else {
                return null;
            }
        }

        const res = cellsArrDivided.map((elem, index) => {
           for(let i = 0; i < elem.length; i++) {
           return <tr key={index}> 
           <td key={elem[0].id} className={styleForToday(elem[0])}>{elem[0].value}</td><td key={elem[1].id} className={styleForToday(elem[1])}>{elem[1].value}</td> 
           <td key={elem[2].id} className={styleForToday(elem[2])}>{elem[2].value}</td><td key={elem[3].id} className={styleForToday(elem[3])}>{elem[3].value}</td>
           <td key={elem[4].id} className={styleForToday(elem[4])}>{elem[4].value}</td><td key={elem[5].id} className={styles.isWeekend}>{elem[5].value}</td>
           <td key={elem[6].id} className={"isWeekend"}>{elem[6].value}</td>
           </tr>
           }
           return elem;
        }) 
        //console.log(res);
        return res;
    }

return <div>
    <h2>{monthObj[month][0]} {year}</h2>
    <table className={styles[monthObj[month][1]]}>
        <thead>
            <tr>
                <td>Пн</td><td>Вт</td><td>Ср</td><td>Чт</td><td>Пт</td> 
                <td className={styles.isWeekend}>Сб</td><td className={styles.isWeekend}>Нд</td>
            </tr>
        </thead>
        <tbody>
           <Rows />
        </tbody>
    </table>
    <div className={styles.buttons}>
        <button onClick={handleClickBack}>Назад</button>
        <button onClick={handleClickForward}>Вперед</button>
    </div>
</div>
}

export default CalendarPresent;
