import React, {useState, useRef} from "react";
import ChecklistPresent from "./ChecklistPresent";
import styles from './ChecklistPresent.module.css';

function Checklist({month, year, eventId, eventText, visability, setVisability, cellsArr, setCellsArr, arrValues, setArrValues}) {

    const [inputValue, setInputValue] = useState('');
    //const [arrValues, setArrValues] = useState([]);
    //const [counterValues, setCounterValues] = useState(-1);
    const refInput = useRef(null); 

    function handlerInputChange(event) {
        setInputValue(event.target.value);
    }

    function createArrValues() {
        const copy = [...arrValues];
        copy.push(inputValue);
        setArrValues(copy);
        setInputValue('');
       // setCounterValues(counterValues + 1);
        console.log(arrValues);
    }

    function handleClickDoneButton(event) {
        event.target.parentNode.firstChild.classList.toggle(styles.styleLiDone);
    }

    function handleClickDeleteButton(event) {
        const copy = [...arrValues];
        copy.splice(copy.indexOf(event.target.parentNode.firstChild.textContent), 1);
        setArrValues(copy);
    }

    function handleClickChangeButton(event) {
        setInputValue(event.target.parentNode.firstChild.textContent);
        const copy = [...arrValues];
        copy.splice(copy.indexOf(event.target.parentNode.firstChild.textContent), 1);
        setArrValues(copy);
        refInput.current.focus();
        console.log(arrValues);
    }

    function handleClickSaveButton() {
        setVisability(false);
        if(arrValues.length >= 1) {
            localStorage.setItem(eventId, JSON.stringify(arrValues));
        }
        const copyCellsArr = [...cellsArr];
        for(let elem of copyCellsArr) {
            if(elem.id === eventId) {
                elem.hasTasks = true;
                console.log(elem);
            }
        }
        setCellsArr(copyCellsArr);

        const copyArrValues = [...arrValues];
        copyArrValues.length = 0;
        setArrValues(copyArrValues);


        console.log(localStorage.getItem(eventId));
        console.log(cellsArr);
        console.log(arrValues);
    }

    function CreateLi() {
        const res = arrValues.map((elem, index) => {
            return <div key={index}>
            <li>{elem}</li>
            <button onClick={handleClickChangeButton} className={styles.styleLiButtons}>Ред</button>
            <button onClick={handleClickDeleteButton} className={styles.styleLiButtons}>Х</button>
            <button onClick={handleClickDoneButton} className={styles.styleLiButtons}>V</button>
            <br /> <br />
            </div>
        })
        return res;
    }

    return <div>
       <ChecklistPresent month={month} year={year} eventId={eventId} eventText={eventText} inputValue={inputValue} handlerInputChange={handlerInputChange} createArrValues={createArrValues} CreateLi={CreateLi} visability={visability} handleClickSaveButton={handleClickSaveButton} refInput={refInput} /> 
    </div>

}


export default Checklist;
