import React from "react";
import styles from './ChecklistPresent.module.css';


function ChecklistPresent({month, year, eventId, eventText, inputValue, createArrValues, handlerInputChange, CreateLi}) {

function addZero(num) {
   if (num >= 1 && num <= 9) {
    return '0' + num;
   } else {
    return num;
   }
}

return <div className={styles.container}>
    <h3>Список справ на {eventText}.{addZero(month)}.{year}</h3>
    <input value={inputValue} onChange={handlerInputChange} onBlur={createArrValues} />
    <ul>
    <CreateLi />
    </ul>
</div>

}

export default ChecklistPresent;
