import React, {useState} from "react";
import CalendarPresent from "./CalendarPresent";
import {nanoid} from 'nanoid';

function Calendar() {
    const now = new Date();
    const [year, setYear] = useState(now.getFullYear());
    const [month, setMonth] = useState(now.getMonth());
    const [date, setDate] = useState(now.getDate());
    const [day, setDay] = useState(now.getDay());
    const [cellsAmount, setCellsAmount] = useState(null);
    const [cellsArr, setCellsArr] = useState([]);
    const lastDate = new Date(year, month + 1, 0);
    const lastDay = lastDate.getDate();
    
    function getCellsAmount() {
        let res = day + lastDay;
        while(res % 7 !== 0) {
            res += 1;
        }
        setCellsAmount(res);
    }

    function createCellsArr() {
        getCellsAmount();
        const copy = [...cellsArr];
        for(let i = 1; i <= cellsAmount; i++) {
            copy.push({
                id: nanoid(),
                value: '',
                hasTasks: false, 
                tasksDone: false,
            })
        }
        setCellsArr(copy);
        console.log(cellsArr);
    }



    return <div>
        <CalendarPresent year = {year} month = {month} lastDay = {lastDay} createCellsArr={createCellsArr} />
    </div>

}

export default Calendar;
