import React from "react";
import styles from './CalendarPresent.module.css';

function CalendarPresent({year, month, lastDay, createCellsArr, day, setCellsAmount}) {
    const monthObj = {
        0: 'Січень',
        1: 'Лютий',
        2: 'Березень',
        3: 'Квітень',
        4: 'Травень',
        5: 'Червень',
        6: 'Липень',
        7: 'Серпень',
        8: 'Вересень',
        9: 'Жовтень',
        10: 'Листопад',
        11: 'Грудень',

    }

    function getCellsAmount() {
        let res = day + lastDay;
        while(res % 7 !== 0) {
            res += 1;
        }
        setCellsAmount(res);
    }

    function createTable() {
        getCellsAmount();
        createCellsArr();
    }

return <div>
    <h2>{monthObj[month]} {year}</h2>
    <table>
        <tbody>
            {createTable()}
        </tbody>
    </table>
    <div className={styles.buttons}>
        <button>Назад</button>
        <button>Вперед</button>
    </div>
</div>
}

export default CalendarPresent;
