import React, {useState} from "react";
import CalendarPresent from "./CalendarPresent";
import {nanoid} from 'nanoid';

function Calendar() {
    const now = new Date();
    const [year, setYear] = useState(now.getFullYear());
    const [month, setMonth] = useState(now.getMonth());
    const [date, setDate] = useState(now.getDate());
    const [day, setDay] = useState(now.getDay());
    const lastDate = new Date(year, month + 1, 0);
    const lastDay = lastDate.getDate();
    const [cellsAmount, setCellsAmount] = useState(day + lastDay);
    const [cellsArr, setCellsArr] = useState([]);

    cellsAmount % 7 !== 0 && setCellsAmount(cellsAmount + 1);
   
   
    const copy = [...cellsArr];
    for(let i = cellsAmount; i >= 1; i--) {
        copy.push({
            id: nanoid(),
            value: '',
            hasTasks: false,
            allTasksDone: false,
        })
    }
    console.log(copy);
    


    return <div>
        <CalendarPresent year = {year} month = {month} lastDay = {lastDay} day = {day} cellsAmount = {cellsAmount} />
        
    </div>

}

export default Calendar;
