import React, {useState} from "react";
import ChecklistPresent from "./ChecklistPresent";

function Checklist({month, year, eventId, eventText, visability, handleClickSaveButton}) {

    const [inputValue, setInputValue] = useState('');
    const [arrValues, setArrValues] = useState([]);
    const [counerValues, setCounterValues] = useState(-1);

    function handlerInputChange(event) {
        setInputValue(event.target.value);
    }

    function createArrValues() {
        const copy = [...arrValues];
        copy.push(inputValue);
        setArrValues(copy);
        setInputValue('');
        setCounterValues(counerValues + 1);
        console.log(arrValues);
    }

    function CreateLi() {
        const res = arrValues.map((elem, index) => {
            return <li key={index}>
                {elem}
            </li>
        })
        return res;
    }

    return <div>
       <ChecklistPresent month={month} year={year} eventId={eventId} eventText={eventText} inputValue={inputValue} handlerInputChange={handlerInputChange} createArrValues={createArrValues} CreateLi={CreateLi} visability={visability} handleClickSaveButton={handleClickSaveButton} /> 
    </div>

}


export default Checklist;
