import React, {useState} from "react";
import ChecklistPresent from "./ChecklistPresent";

function Checklist({month, year, eventId, eventText}) {

    const [inputValue, setInputValue] = useState('');
    const [arrValues, setArrValues] = useState([]);

    function handlerInputChange(event) {
        setInputValue(event.target.value);
    }

    function createArrValues() {
        const copy = [...arrValues];
        copy.push(inputValue);
        setArrValues(copy);
        setInputValue('');
        console.log(arrValues);
    }

    return <div>
       <ChecklistPresent month={month} year={year} eventId={eventId} eventText={eventText} handlerInputChange={handlerInputChange} createArrValues={createArrValues} /> 
    </div>

}


export default Checklist;
